vbox.script:
var number = 0;
var operation;
var clear = true;
function addDigit(digit) {
	if (clear == true) {
		display.text = "";
		clear = false;
	}
	display.text += digit;
}

function performOperation(op) {
	if (display.text.length == 0) {
		return;
	}

	if (operation == null) {
		number = display.text;
		operation = op;
		clear = true;
	} else {
		calc();
		performOperation(op);
	}
}

function calc() {
	if (display.text.length == 0) {
		return;
	}

	if (operation != null) {
		var c;
		if (operation == "+") {
			c = Std.parseFloat(number) + Std.parseFloat(display.text);
		} else if (operation == "-") {
			c = Std.parseFloat(number) - Std.parseFloat(display.text);
		} else if (operation == "*") {
			c = Std.parseFloat(number) * Std.parseFloat(display.text);
		} else if (operation == "/") {
			c = Std.parseFloat(number) / Std.parseFloat(display.text);
		}

		number = c;
		operation = null;
		display.text = "" + c;
		clear = true;
	}
}
